let rec pos = function
	| [] -> (0,0)
	| (a,b) :: rest ->
		let addtuple (a,b) (c,d) = (a+c,b+d) in
			if a = "forward" then
				addtuple (pos rest) (b, 0)
			else if a = "down" then
				addtuple (pos rest) (0, b)
			else if a = "up" then
				addtuple (pos rest) (0, -b)
			else
				raise (Foo "Oh no!");;

