let rec pos2 = function
	| [] -> (0,0,0)
	| (a,b) :: rest ->
		let addtuple (a,b,e) (c,d,f) = (a+c,b+d,e+f) in
		let (x,y,z) = pos2 rest in
			if a = "forward" then
				addtuple (x,y,z) (b, b*z, 0)
			else if a = "down" then
				addtuple (x,y,z) (0, 0, b)
			else if a = "up" then
				addtuple (x,y,z) (0, 0, -b)
			else
				raise (Foo "Oh no!");;
			
