let explode s = List.init (String.length s) (String.get s)

let score = function
	| ')' -> 3
	| ']' -> 57
	| '}' -> 1197
	| '>' -> 25137

let rec check stack line = match(stack, line) with
	| _, [] -> 0
	| stack, l::line ->
		if l = '(' || l = '[' || l = '{' || l = '<' then check (l::stack) line
		else if List.length stack = 0 then
			score l
		else 
			let s = List.hd stack in
			if l = ')' && s = '(' || l = ']' && s = '[' || l = '}' && s = '{' || l = '>' && s = '<' then
				check (List.tl stack) line
			else
				score l
				

